mapper.update:
update board_star
set star_short_review = #{starShortReview},
    star_rating = #{starRating}
where board_id = #{boardId} and star_type = #{starType}

mapper.select:
select board_id
from board_star
where star_type = #{typeName}
order by star_rating
limit 5 offset #{offset}

