mapper.update:
update board_star
set board_star_short_review = #{boardStarShortReview},
    board_star_rating = #{starRating}
where board_id = #{boardId} and board_star_type = #{starType}

mapper.select:
select board_id from board_star where board_star_type = #{searchConditionDTO.sortType} and exists( select 1 from board b where b.board_is_delete = 0 and b.board_id = board_star.board_id
and b.board_region = #{searchConditionDTO.searchText}
and match(b.board_title) against (#{searchConditionDTO.searchText})
)
order by board_star_rating desc
order by board_star_rating
limit 5 offset #{offset}
