mapper.select:
select exists(
    select 1
    from member
    where nickname = #{nickname}
);

select exists(
    select 1
    from member
    where email = #{email}
);

select id
from member
where nickname = #{nickname}

select nickname
from member
where id = #{id}

select email
from member
where refresh_token = #{refreshToken};

mapper.update:
update member
set refresh_token = #{refreshToken}
where email = #{email}

update member
set refresh_token = null
where email = #{memberEmail};
