mapper.select:
select exists(
    select 1
    from member
    where nickname = #{nickname}
);

select exists(
    select 1
    from member
    where email = #{email}
);

select id, password, name, email, phone_number as phoneNumber, nickName
from member
where email = #{email}

select email
from member
where refresh_token = #{refreshToken};

mapper.update:
update member
set refresh_token = #{refreshToken}
where email = #{email}
